import pandas as pd

def carregar_dades():
    data = {'Nom': ['John', 'Mary', 'David'], 'Edat': [25, 31, 42]}
    return pd.DataFrame(data)

def transformar_dades(df):
    df['Edat_al_quadrat'] = df['Edat'].apply(lambda x: x**2)
    return df

df = carregar_dades()
df = transformar_dades(df)
print(df)
